$(document).ready(function(){var o=/^([a-zA-Z\s]{3,})*$/;$("#name, #title, #content").on("keypress keydown keyup",function(){$(this).val().match(o)?$(this).css("border-bottom-color","#7dbd6e"):$(this).css("border-bottom-color","red")});var t=/^[a-zA-Z0-9\.]+@[a-zA-Z0-9]+.[a-zA-Z0-9]*$/;$("#mail").on("keypress keydown keyup",function(){$(this).val().match(t)?$(this).css("border-bottom-color","#7dbd6e"):$(this).css("border-bottom-color","red")});var e=/\(?([0-9]{3})\)?([ .-]?)([0-9]{3})\2([0-9]{3,10})/;$("#phone").on("keypress keydown keyup",function(){$(this).val().match(e)?$(this).css("border-bottom-color","#7dbd6e"):$(this).css("border-bottom-color","red")})});